import {
  RecoilRoot,
  useRecoilState,
  atom,
  selector

} from 'recoil';

function App() {
  return (
    <RecoilRoot>
      <TextInput />
    </RecoilRoot>
  );
}

const textInputState = atom({
  key: "textInputState",
  default: "",
})

function TextInput(){

  const [ text, setText ] = useRecoilState(textInputState)

  const onInputChange = (input) => {
    setText(input.target.value)
  }

  return (
    <div>
      <input value={text} onChange={onInputChange} />
    </div>
  )
}

